$(document).ready(function(){
	$(".form_1").mouseleave(function(){
		$(".form_1").css("background-color","#ff00bf");
	});
	$(".form_1").mouseenter(function(){
		$(".form_1").css("background-color","#8c7373");
	});
	$(".form_1").mouseleave(function(){
		$(".form_1,p").css("font-size","25px");
	});
	$(".form_1").mouseenter(function(){
		$(".form_1,p").css("font-size","50px");
	});

});